use std::net::{IpAddr, Ipv6Addr};
use rocket::data::{Limits, ToByteUnit};
use rocket::{get, routes};
use rocket::config::Config as RocketConfig;
use rocket::Rocket;
use rocket::Build;
use clap::Parser;

use cryptography_service;

#[get("/key/<seed>")]
async fn key(seed: &str) -> String{
let (pks, sks) = cryptography_service::mldsa2_keygen(Some(seed));
return format!("((private \"{}\") (public \"{}\"))", sks, pks);
}

#[get("/sign/<key>/<message>")]
async fn sign(key: &str, message: &str) -> String{
let mut sig = r#"""#.to_owned();
sig.push_str(&cryptography_service::mldsa2_sign(key.to_string(), message));
sig.push_str(r#"""#);
return sig
}

#[get("/verify/<key>/<signature>/<message>")]
async fn verify(key: &str, signature: &str, message: &str) -> String {
let c = cryptography_service::mldsa2_verify(key.to_string(), message, signature.to_string()).unwrap();
if c == () {
   return "#t".to_string();
}
else{
   return "#f".to_string();
}
}


/// Simple program to greet a person
#[derive(Parser, Debug)]
#[command(version, about, long_about = None)]
struct Args {
    /// Port number on which the server should be launched
    #[arg(short, long, default_value_t = 8887)]
    port: u16,

}

#[rocket::main]
async fn main() {
    let args = Args::parse();
	server(args.port).launch().await.unwrap();
}


fn server(port: u16) -> Rocket<Build>{
    let mut rocket_config = RocketConfig::default();
    rocket_config.port = port;
    rocket_config.address = IpAddr::V6(Ipv6Addr::new(0, 0, 0, 0, 0, 0, 0, 0));
    rocket_config.limits = Limits::new().limit("string", 1_i32.mebibytes());

    rocket::custom(&rocket_config)
    .mount("/signature", routes![
        key,
        sign,
        verify
    ])
}


#[cfg(test)]
mod test {
    //use rocket::uri;
    use super::server;
    use rocket::local::blocking::Client;
    use rocket::http::Status;

    #[test]
    fn web_api_test() {
        let client = Client::tracked(server(8887)).expect("valid rocket instance");
        let response = client.get("/signature/key/baaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaad").dispatch();
        assert_eq!(response.status(), Status::Ok);
        assert_eq!(response.into_string().unwrap(), "((private \"\") (public \"\"))");

        let response = client.get("").dispatch();
        assert_eq!(response.status(), Status::Ok);
        assert_eq!(response.into_string().unwrap(), "\"\"");

        let response = client.get("").dispatch();
        assert_eq!(response.status(), Status::Ok);
        assert_eq!(response.into_string().unwrap(), "#t");
    }
}
